mapper.update:
UPDATE watched_movie_record
SET movie_no = #{movieNo} ,memo = #{memo} ,#{watchedDate}
WHERE record_no = #{recordNo}

mapper.select:
SELECT COUNT(DISTINCT r.movie_no) FROM watched_movie_record r JOIN movie_dto md ON r.movie_no = md.id JOIN movie_genre mg ON md.id = mg.movie_id JOIN genre_dto gd ON mg.genre_id = gd.id
AND r.member_no = #{memberNo}
AND md.title LIKE CONCAT('%', #{movieTitle}, '%')
AND gd.id = #{genreType}

SELECT DISTINCT r.record_no ,r.movie_no ,r.member_no ,memo ,r.watched_date ,md.poster_path ,md.title FROM watched_movie_record r JOIN movie_dto md ON r.movie_no = md.id JOIN movie_genre mg ON md.id = mg.movie_id JOIN genre_dto gd ON mg.genre_id = gd.id
member_no = #{memberNo}
AND md.title LIKE CONCAT('%', #{movieTitle}, '%')
AND gd.id = #{genreType}
ORDER BY r.watched_date DESC LIMIT #{limitPageNo}, #{numPerPage}
